def replace_icons(match):
    full_str = match.group(0)
    class_attr = match.group(1)
    icon_name = match.group(2).strip()
    
    # Check if we have an exact match in our map, else fallback to a generic shape
    if icon_name not in svg_map:
        return full_str
    
    # For size classes, Heroicons usually needs an explicit w-6 h-6, let's inject it if not present
    classes = class_attr.replace('material-icons-outlined', '')
    if 'w-' not in classes:
        classes += ' w-6 h-6'
        
    svg_str = svg_map[icon_name].replace('{{attrs}}', f'class="{classes.strip()}"')
    return svg_str

for fn in glob.glob('*.html'):
    with open(fn, 'r', encoding='utf-8') as f:
        content = f.read()

    # Regex to find <span class="...material-icons-outlined...">icon_name</span>
    # and <span class='...material-icons-outlined...'>icon_name</span>
    content = re.sub(r'<span\s+class="([^"]*material-icons-outlined[^"]*)">([^<]+)</span>', replace_icons, content)

    # Remove the google fonts link for icons
    content = re.sub(r'<link[^>]+family=Material\+Icons\+Outlined[^>]+>\s*', '', content)
    
    with open(fn, 'w', encoding='utf-8') as f:
        f.write(content)

print("Icons replaced with inline SVGs successfully.")
